val UUID_KEY = "UUID"
val DEVICE_ID_KEY = "DEVICE_ID"
val SESSION = "SESSION"
val LANG = "LANG"
val EMAIL = "EMAIL"
val NAME = "NAME"
val SURNAME = "SURNAME"
val AVATAR_KEY = "AVATAR"
val INSTA_CLIENT_ID_KEY = "INSTA_CLIENT_ID_KEY"
val INSTA_ADDRESS = "INSTA_ADDRESS"
val SUBSCRIPTION_KEY = "SUBSCRIPTION_KEY"
val PROMOCODE_KEY = "PROMOCODE"
val EXTRA_INSTAGRAM_RESULT = "EXTRA_INSTAGRAM_RESULT"

fun readParam(context: Context, KEY: String, DEFAULT: String): String {
    val sharedPref = context.getSharedPreferences(PREFS_FILENAME, 0) ?: return ""
    var value = sharedPref.getString(KEY, "")
    if (value == null) {
        value = DEFAULT;
        with(sharedPref.edit()) {
            putString(KEY, value)
            commit()
        }
    }
    return value
}

fun getUUID(context: Context): String {
    var result = readParam(context, UUID_KEY, UUID.randomUUID().toString())
    if (result.isEmpty()){
        result = UUID.randomUUID().toString()
        setUUID(context,result)
    }
    return result
}

fun getDeviceID(context: Context): String {
    var result =  readParam(context, DEVICE_ID_KEY, UUID.randomUUID().toString())
    if (result.isEmpty()){
        result = UUID.randomUUID().toString()
        setDeviceID(context,result)
    }
    return result
}

fun getSession(context: Context): String {
    return readParam(context, SESSION, UUID.randomUUID().toString())
}

fun getLang(context: Context): String {
    var result =readParam(context, LANG, "ru-RU")
    if (result.isEmpty()){
        result = "ru-RU"
        setLang(context,result)
    }
    return result
}

fun getEmail(context: Context): String {
    return readParam(context, EMAIL, "")
}

fun getName(context: Context): String {
    return readParam(context, NAME, "")
}

fun getSurname(context: Context): String {
    return readParam(context, SURNAME, "")
}

val SHARED_KEY_IS_LOGGED_IN = "isLoggedIn"

fun getLoggedIn(context: Context): Boolean {
    val sharedPref = context.getSharedPreferences(PREFS_FILENAME, 0) ?: return false
    return sharedPref.getBoolean(SHARED_KEY_IS_LOGGED_IN, false)
}

fun setLoggedIn(context: Context, flg:Boolean){
    val sharedPref = context.getSharedPreferences(PREFS_FILENAME, 0) ?: return
    with(sharedPref.edit()) {
        putBoolean(com.f2f.face2face.SHARED_KEY_IS_LOGGED_IN, flg)
        commit()
    }
}

fun setSession(context: Context, session:String){
    val sharedPref = context.getSharedPreferences(PREFS_FILENAME, 0) ?: return
    with(sharedPref.edit()) {
        putString(SESSION, session)
        commit()
    }
}

