fun getSubscriptionKey(context:Context):Boolean {
    val sharedPref = context.getSharedPreferences(PREFS_FILENAME, 0) ?: return false
    if (!sharedPref.getBoolean(SUBSCRIPTION_KEY, false)){
        with(sharedPref.edit()) {
            putBoolean(SUBSCRIPTION_KEY, true)
            commit()
        }
        return false
    }
    return true
}

